import { motion } from 'framer-motion';

function Skills() {
  const skills = [
    { name: 'HTML', icon: 'fab fa-html5', progress: 95 },
    { name: 'CSS', icon: 'fab fa-css3-alt', progress: 90 },
    { name: 'JavaScript', icon: 'fab fa-js', progress: 85 },
    { name: 'Team Collaboration', icon: 'fas fa-people-arrows', progress: 80 }
  ];

  const skillVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0 }
  };

  return (
    <motion.section
      id="skills"
      className="section skills"
      initial={{ opacity: 0, y: 30 }}
      whileInView={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.8, delay: 0.3 }}
      viewport={{ once: true }}
    >
      <motion.h2
        initial={{ opacity: 0, x: -20 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ duration: 0.6 }}
      >
        My Skills
      </motion.h2>
      <div className="skills-grid">
        {skills.map((skill, index) => (
          <motion.div
            key={skill.name}
            className="skill"
            variants={skillVariants}
            initial="hidden"
            whileInView="visible"
            transition={{ duration: 0.6, delay: index * 0.2 }}
            viewport={{ once: true }}
          >
            <i className={`${skill.icon} skill-icon`}></i>
            <span>{skill.name}</span>
            <div className="skill-bar">
              <motion.div
                className="progress"
                initial={{ width: 0 }}
                animate={{ width: `${skill.progress}%` }}
                transition={{ duration: 1.5, ease: [0.68, -0.55, 0.27, 1.55], delay: index * 0.3 }}
              ></motion.div>
            </div>
          </motion.div>
        ))}
      </div>
    </motion.section>
  );
}

export default Skills;
